    }
  });

  // Mistakes routes
  app.get('/api/mistakes', authenticateToken, async (req: AuthenticatedRequest, res) => {
    try {
      const mistakes = await storage.getMistakes(req.user!.id);
      res.json(mistakes);
    } catch (error: any) {
