  const [responses, setResponses] = useState<Record<string, string>>({});
  const [selectedOption, setSelectedOption] = useState('');
  const [isStarted, setIsStarted] = useState(false);
  const [isCompleted, setIsCompleted] = useState(false);

  const { data: latestAssessment } = useQuery({
    queryKey: ['/api/ai-assessments/latest'],
  });

  const { data: allAssessments } = useQuery({
    queryKey: ['/api/ai-assessments'],
  });

  const createAssessmentMutation = useMutation({
    mutationFn: (data: { responses: Record<string, string> }) => 
      apiRequest('POST', '/api/ai-assessments', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/ai-assessments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/ai-assessments/latest'] });
      setIsCompleted(true);
      toast({
        title: 'Assessment Complete!',
        description: 'Your personal growth assessment has been generated.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to complete assessment',
        variant: 'destructive',
      });
    },
  });

  const handleStart = () => {
    setIsStarted(true);
    setCurrentQuestion(0);
    setResponses({});
    setSelectedOption('');
    setIsCompleted(false);
  };

  const handleOptionSelect = (value: string) => {
    setSelectedOption(value);
  };

  const handleNext = () => {
    if (!selectedOption) return;

    const questionKey = `Question ${currentQuestion + 1}`;
    const selectedOptionLabel = questions[currentQuestion].options.find(
      opt => opt.value === selectedOption
    )?.label || selectedOption;

    const newResponses = {
      ...responses,
      [questionKey]: selectedOptionLabel
    };
    setResponses(newResponses);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption('');
    } else {
      // Complete the assessment
      createAssessmentMutation.mutate({ responses: newResponses });
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
      const prevQuestionKey = `Question ${currentQuestion}`;
      const prevResponse = responses[prevQuestionKey];
      const prevOption = questions[currentQuestion - 1].options.find(
        opt => opt.label === prevResponse
      )?.value || '';
      setSelectedOption(prevOption);
    }
  };

  const progress = isCompleted ? 100 : ((currentQuestion + (selectedOption ? 1 : 0)) / questions.length) * 100;

  if (!isStarted) {
    return (
      <div className="p-6 space-y-6">
        <div className="flex items-center justify-between">
          <h1 className="text-3xl font-bold text-foreground">AI Personal Growth Assessment</h1>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Start Assessment Card */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-3">
                <Brain className="text-primary" size={24} />
                <span>5-Minute Growth Assessment</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="bg-muted rounded-lg p-6 space-y-4">
                <div className="flex items-center space-x-3">
                  <Clock className="text-chart-1" size={20} />
                  <span className="font-medium text-card-foreground">Quick & Insightful</span>
                </div>
                <p className="text-muted-foreground">
                  Take a brief 5-question assessment to get personalized insights about your growth journey.
                </p>
              </div>

              <div className="space-y-3">
                <h3 className="font-semibold text-card-foreground">What you'll get:</h3>
                <div className="space-y-2">
                  <div className="flex items-center space-x-3">
                    <CheckCircle className="text-chart-2" size={16} />
                    <span className="text-sm text-muted-foreground">Personalized growth score (0-100)</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <CheckCircle className="text-chart-2" size={16} />
                    <span className="text-sm text-muted-foreground">AI-powered recommendations</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <CheckCircle className="text-chart-2" size={16} />
                    <span className="text-sm text-muted-foreground">Progress tracking over time</span>
                  </div>
                </div>
              </div>

              <Button 
                className="w-full" 
                onClick={handleStart}
                data-testid="button-start-assessment"
              >
                <Brain className="mr-2" size={16} />
                Start Assessment
              </Button>
            </CardContent>
          </Card>

          {/* Latest Results Card */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-3">
                <TrendingUp className="text-chart-4" size={24} />
                <span>Your Latest Results</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {latestAssessment ? (
                <div className="space-y-6">
                  <div className="text-center">
                    <div className="text-4xl font-bold text-chart-4 mb-2">
                      {latestAssessment.growthScore}
                    </div>
                    <p className="text-muted-foreground">Growth Score</p>
                    <p className="text-xs text-muted-foreground mt-1">
                      {format(parseISO(latestAssessment.createdAt), 'MMM d, yyyy')}
                    </p>
                  </div>

                  <Progress value={latestAssessment.growthScore} className="h-3" />

                  <div className="bg-muted rounded-lg p-4">
                    <h4 className="font-medium text-card-foreground mb-2">AI Recommendations</h4>
                    <p className="text-sm text-muted-foreground line-clamp-4">
                      {latestAssessment.recommendations}
                    </p>
                  </div>
                </div>
              ) : (
                <div className="text-center py-8">
                  <Brain className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground">
                    Take your first assessment to see your growth insights here
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Assessment History */}
        {allAssessments && allAssessments.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Assessment History</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {allAssessments.slice(0, 5).map((assessment: any) => (
                  <div
                    key={assessment.id}
                    className="flex items-center justify-between p-4 bg-muted rounded-lg"
                    data-testid={`assessment-${assessment.id}`}
                  >
                    <div>
                      <div className="flex items-center space-x-3">
                        <div className="text-lg font-semibold text-card-foreground">
                          Score: {assessment.growthScore}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          {format(parseISO(assessment.createdAt), 'MMM d, yyyy')}
                        </div>
                      </div>
                    </div>
                    <Progress value={assessment.growthScore} className="w-24 h-2" />
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    );
  }

  if (isCompleted) {
    return (
      <div className="p-6 space-y-6">
        <div className="text-center">
          <CheckCircle className="mx-auto h-16 w-16 text-chart-2 mb-4" />
          <h1 className="text-3xl font-bold text-foreground mb-2">Assessment Complete!</h1>
          <p className="text-muted-foreground">
            Your personal growth assessment has been generated and saved.
          </p>
        </div>

        <Card className="max-w-2xl mx-auto">
          <CardContent className="p-8 text-center space-y-6">
            <div>
              <div className="text-5xl font-bold text-chart-4 mb-2">
                {latestAssessment?.growthScore || 'Processing...'}
              </div>
              <p className="text-muted-foreground">Your Growth Score</p>
            </div>

            {latestAssessment && (
              <>
                <Progress value={latestAssessment.growthScore} className="h-4" />

                <div className="bg-muted rounded-lg p-6 text-left">
                  <h3 className="font-semibold text-card-foreground mb-3">AI Recommendations</h3>
                  <p className="text-muted-foreground whitespace-pre-wrap">
                    {latestAssessment.recommendations}
                  </p>
                </div>
              </>
            )}

            <div className="flex space-x-4 justify-center">
              <Button 
                onClick={() => setIsStarted(false)} 
                variant="outline"
                data-testid="button-view-results"
              >
                View All Results
              </Button>
              <Button 
                onClick={handleStart}
                data-testid="button-retake-assessment"
              >
                Take Again
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold text-foreground">AI Assessment</h1>
        <div className="text-sm text-muted-foreground">
          Question {currentQuestion + 1} of {questions.length}
        </div>
      </div>

      <Card className="max-w-2xl mx-auto">
        <CardHeader>
          <div className="space-y-4">
            <Progress value={progress} className="h-2" />
            <CardTitle className="text-xl">
              {questions[currentQuestion].question}
            </CardTitle>
          </div>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="space-y-3">
            {questions[currentQuestion].options.map((option) => (
              <button
                key={option.value}
                onClick={() => handleOptionSelect(option.value)}
                className={`
                  w-full text-left p-4 rounded-lg transition-colors border
                  ${selectedOption === option.value 
                    ? 'bg-primary text-primary-foreground border-primary' 
                    : 'bg-accent hover:bg-primary hover:text-primary-foreground border-border'
                  }
                `}
                data-testid={`option-${option.value}`}
              >
                <div className="flex items-center space-x-3">
                  <CheckCircle 
                    className={selectedOption === option.value ? 'text-primary-foreground' : 'text-muted-foreground'}
                    size={16} 
                  />
                  <span className="font-medium">{option.label}</span>
                </div>
              </button>
            ))}
          </div>

          <div className="flex justify-between items-center pt-4">
            <Button
              variant="outline"
              onClick={handlePrevious}
              disabled={currentQuestion === 0}
              data-testid="button-previous"
            >
              <ArrowLeft className="mr-2" size={16} />
              Previous
            </Button>

            <Button
              onClick={handleNext}
              disabled={!selectedOption || createAssessmentMutation.isPending}
              data-testid="button-next"
            >
              {currentQuestion === questions.length - 1 ? (
                createAssessmentMutation.isPending ? (
                  'Generating Results...'
                ) : (
                  'Complete Assessment'
                )
              ) : (
                <>
                  Next
                  <ArrowRight className="ml-2" size={16} />
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default AIInterview;

client/src/pages/Achievements.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Trophy, Plus, Edit, Trash2, Calendar, Star } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { format, parseISO } from 'date-fns';
import { formatDateForInput, formatDateForDisplay, getCurrentIST } from '@/lib/timeUtils';

const Achievements = () => {
  const { user } = useAuth();
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingAchievement, setEditingAchievement] = useState<any>(null);

  const [achievementForm, setAchievementForm] = useState({
    title: '',
    description: '',
    category: '',
    date: formatDateForInput(getCurrentIST())
  });

  const { data: achievements, isLoading } = useQuery({
    queryKey: ['/api/achievements'],
  });

  const createAchievementMutation = useMutation({
    mutationFn: (data: any) => apiRequest('POST', '/api/achievements', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/achievements'] });
      setIsDialogOpen(false);
      resetForm();
      toast({
        title: 'Success',
        description: 'Achievement recorded successfully',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const updateAchievementMutation = useMutation({
    mutationFn: ({ id, ...data }: any) => apiRequest('PUT', `/api/achievements/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/achievements'] });
      setIsDialogOpen(false);
      setEditingAchievement(null);
      resetForm();
      toast({
        title: 'Success',
        description: 'Achievement updated successfully',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const deleteAchievementMutation = useMutation({
    mutationFn: (id: string) => apiRequest('DELETE', `/api/achievements/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/achievements'] });
      toast({
        title: 'Success',
        description: 'Achievement deleted successfully',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const resetForm = () => {
    setAchievementForm({
      title: '',
      description: '',
      category: '',
      date: formatDateForInput(getCurrentIST())
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const achievementData = {
      ...achievementForm,
      date: new Date(achievementForm.date).toISOString(),
    };

    if (editingAchievement) {
      updateAchievementMutation.mutate({ id: editingAchievement.id, ...achievementData });
    } else {
      createAchievementMutation.mutate(achievementData);
    }
  };

  const handleEdit = (achievement: any) => {
    setEditingAchievement(achievement);
    setAchievementForm({
      title: achievement.title,
      description: achievement.description,
      category: achievement.category || '',
      date: formatDateForInput(achievement.date)
    });
    setIsDialogOpen(true);
  };

  const handleDelete = (id: string) => {
    if (confirm('Are you sure you want to delete this achievement?')) {
      deleteAchievementMutation.mutate(id);
    }
  };

  const categoryColors: Record<string, string> = {
    'personal': 'bg-chart-1 text-white',
    'professional': 'bg-chart-2 text-white',
    'academic': 'bg-chart-3 text-white',
    'fitness': 'bg-chart-4 text-white',
    'creative': 'bg-chart-5 text-white',
    'default': 'bg-primary text-primary-foreground'
  };

  const getCategoryColor = (category: string) => {
    const normalizedCategory = category?.toLowerCase() || '';
    return categoryColors[normalizedCategory] || categoryColors['default'];
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-muted rounded w-1/3"></div>
          <div className="grid gap-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-32 bg-muted rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold text-foreground">Achievements</h1>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-new-achievement">
              <Plus className="mr-2" size={16} />
              Add Achievement
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>
                {editingAchievement ? 'Edit Achievement' : 'Add New Achievement'}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="title">Title</Label>
                <Input
                  id="title"
                  value={achievementForm.title}
                  onChange={(e) => setAchievementForm({ ...achievementForm, title: e.target.value })}
                  placeholder="What did you accomplish?"
                  required
                  data-testid="input-achievement-title"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="date">Date</Label>
                  <Input
                    id="date"
                    type="date"
                    value={achievementForm.date}
                    onChange={(e) => setAchievementForm({ ...achievementForm, date: e.target.value })}
                    required
                    data-testid="input-achievement-date"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="category">Category</Label>
                  <Input
                    id="category"
                    value={achievementForm.category}
                    onChange={(e) => setAchievementForm({ ...achievementForm, category: e.target.value })}
                    placeholder="e.g., Personal, Professional, Academic"
                    data-testid="input-achievement-category"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={achievementForm.description}
                  onChange={(e) => setAchievementForm({ ...achievementForm, description: e.target.value })}
                  placeholder="Describe your achievement and why it's meaningful to you..."
                  className="min-h-[120px]"
                  required
                  data-testid="textarea-achievement-description"
                />
              </div>

              <div className="flex justify-end space-x-2">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => {
                    setIsDialogOpen(false);
                    setEditingAchievement(null);
                    resetForm();
                  }}
                  data-testid="button-cancel-achievement"
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createAchievementMutation.isPending || updateAchievementMutation.isPending}
                  data-testid="button-save-achievement"
                >
                  {editingAchievement ? 'Update Achievement' : 'Add Achievement'}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {achievements?.length === 0 ? (
        <Card>
          <CardContent className="text-center py-12">
            <Trophy className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold text-foreground mb-2">No achievements yet</h3>
            <p className="text-muted-foreground mb-4">
              Start celebrating your wins, big and small
            </p>
            <Button onClick={() => setIsDialogOpen(true)} data-testid="button-first-achievement">
              <Plus className="mr-2" size={16} />
              Add First Achievement
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-4">
          {achievements?.map((achievement: any) => (
            <Card key={achievement.id} className="card-hover" data-testid={`achievement-${achievement.id}`}>
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex items-start space-x-3">
                    <div className="w-12 h-12 bg-chart-3 bg-opacity-20 rounded-lg flex items-center justify-center">
                      <Trophy className="text-chart-3" size={20} />
                    </div>
                    <div className="flex-1">
                      <CardTitle className="text-xl mb-2">{achievement.title}</CardTitle>
                      <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                        <div className="flex items-center space-x-1">
                          <Calendar size={14} />
                          <span>{formatDateForDisplay(achievement.date)}</span>
                        </div>
                        {achievement.category && (
                          <span className={`px-2 py-1 rounded text-xs ${getCategoryColor(achievement.category)}`}>
                            {achievement.category}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                  <div className="flex space-x-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleEdit(achievement)}
                      data-testid={`button-edit-achievement-${achievement.id}`}
                    >
                      <Edit size={16} />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleDelete(achievement.id)}
                      data-testid={`button-delete-achievement-${achievement.id}`}
                    >
                      <Trash2 size={16} />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <p className="text-card-foreground whitespace-pre-wrap">{achievement.description}</p>
                <div className="flex items-center mt-4 text-sm text-muted-foreground">
                  <Star className="text-chart-3 mr-1" size={14} />
                  <span>Achievement unlocked!</span>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default Achievements;

client/src/pages/Auth.tsx.content:
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useAuth } from '@/hooks/useAuth';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { ChartLine } from 'lucide-react';
import AnimatedBackground from '@/components/AnimatedBackground';

const signInSchema = z.object({
  email: z.string().email('Invalid email address'),
  password: z.string().min(1, 'Password is required'),
});

const signUpSchema = z.object({
  firstName: z.string().min(1, 'First name is required'),
  lastName: z.string().min(1, 'Last name is required'),
  username: z.string().min(3, 'Username must be at least 3 characters'),
  email: z.string().email('Invalid email address'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  confirmPassword: z.string().min(1, 'Please confirm your password'),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type SignInData = z.infer<typeof signInSchema>;
type SignUpData = z.infer<typeof signUpSchema>;

const Auth = () => {
  const [isLoading, setIsLoading] = useState(false);
  const { signIn, signUp } = useAuth();
  const { toast } = useToast();

  const signInForm = useForm<SignInData>({
    resolver: zodResolver(signInSchema),
    defaultValues: {
      email: '',
      password: '',
    },
  });

  const signUpForm = useForm<SignUpData>({
    resolver: zodResolver(signUpSchema),
    defaultValues: {
      firstName: '',
      lastName: '',
      username: '',
      email: '',
      password: '',
      confirmPassword: '',
    },
  });

  const onSignIn = async (data: SignInData) => {
    setIsLoading(true);
    try {
      await signIn(data.email, data.password);
    } catch (error: any) {
      toast({
        title: 'Sign In Failed',
        description: error.message,
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  const onSignUp = async (data: SignUpData) => {
    setIsLoading(true);
    try {
      await signUp(data);
    } catch (error: any) {
      toast({
        title: 'Sign Up Failed',
        description: error.message,
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
